import { AfterViewInit, Component, OnDestroy, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-search',
  templateUrl: './search.page.html',
  styleUrls: ['./search.page.scss'],
})
export class SearchPage implements AfterViewInit, OnDestroy {
  @ViewChild('searchInput') sInput;
  isLoading: boolean;
  loading = false;
  mode = true;
  query: string = '';
  items: Professional[] = [];
  stack: Professional[] = [
    {
      id: 1,
      name: 'Juan Perez',
      job: 'Ménuisier',
      location: 'Paris',
      photoUrl: 'assets/img/profile.jpg',
      likes: 25,
      rate: 3,
      liked: true,
    },
    {
      id: 2,
      name: 'Juan Perez',
      job: 'Maçon',
      location: 'Paris',
      photoUrl: 'assets/img/profile.jpg',
      likes: 105,
      rate: 5,
      liked: false,
    },
    {
      id: 3,
      name: 'Juan Perez',
      job: 'Ménuisier',
      location: 'Paris',
      photoUrl: '',
      likes: 349,
      rate: 2,
      liked: false,
    },
    {
      id: 4,
      name: 'Juan Perez',
      job: 'Peintre',
      location: 'Paris',
      photoUrl: 'assets/img/profile.jpg',
      likes: 39,
      rate: 4,
      liked: true,
    },
    {
      id: 5,
      name: 'Juan Perez',
      job: 'Ménuisier',
      location: 'Paris',
      photoUrl: null,
      likes: 9,
      rate: 4,
      liked: true,
    },
    {
      id: 6,
      name: 'Juan Perez',
      job: 'Electricien',
      location: 'Paris',
      photoUrl: 'assets/img/profile.jpg',
      likes: 10,
      rate: 4,
      liked: false,
    },
    {
      id: 7,
      name: 'Juan Perez',
      job: 'Plombier',
      location: 'Paris',
      photoUrl: 'assets/img/profile.jpg',
      likes: 60,
      rate: 4,
      liked: false,
    },
    {
      id: 8,
      name: 'Juan Perez',
      job: 'Ménuisier',
      location: 'Paris',
      photoUrl: 'assets/img/profile.jpg',
      likes: 7,
      rate: 4,
      liked: true,
    },
  ];

  private subscriptions: Subscription[] = [];

  constructor(
    private router: Router,
  ) { }

  ngOnInit(){
    setTimeout(()=>{
      this.sInput.setFocus();
    }, 500);
  }
  ngAfterViewInit() { }

  ionViewDidEnter() {
    this.items = this.stack;
  }

  ionViewDidLeave() {
    this.items = [];
    this.subscriptions.forEach(sub => sub.unsubscribe());
  }

  ngOnDestroy() {
    this.subscriptions.forEach(sub => sub.unsubscribe());
  }

  navigate(item: Professional) {
    this.router.navigate(['/pro', item.id]);
  }

  /*onSearchChange(event){
    console.log(event.detail.value);
    this.query=event.detail.value.toLowerCase();
    if(this.query.length  > 0){
      this.items= this.stack.filter((element:any) => {
        return element.location.includes(this.query);
      });
      console.log(this.items);
    }
  }*/

  async onSearchChange(event: any) {
    console.log(event.detail.value);
    this.query = event.detail.value.toLowerCase();
    if (this.query.length > 0) {
      this.isLoading=true;
      this.items = await this.stack.filter((element: Professional) => {
        return element.job.toLowerCase().includes(this.query);
      });
      console.log(this.items);
    } else {
      this.items = [];
    }
  }
}
